package global.namespace.truelicense.build.tasks.generation;

/**
 * Determines how the directory with the files generated by merging the
 * template files with the Velocity context gets added to the maven project.
 *
 * @see GenerateSourcesTask#strategy()
 */
public enum GenerateSourcesStrategy {

    mainSources {
        void generateSourcesUsing(SourceGenerator generator) {
            generator.addMergeDirectoryToCompileSourceRoot();
        }
    },

    testSources {
        void generateSourcesUsing(SourceGenerator generator) {
            generator.addMergeDirectoryToTestCompileSourceRoot();
        }
    };

    abstract void generateSourcesUsing(SourceGenerator generator);
}
